mapper.select:
select 

from mp_role
where roleno = #{roleno,jdbcType=INTEGER}
  and rolename = #{rolename,jdbcType=VARCHAR}

mapper.delete:
delete from mp_role
where roleno = #{roleno,jdbcType=INTEGER}
  and rolename = #{rolename,jdbcType=VARCHAR}

mapper.update:
update mp_role


    locked = #{locked,jdbcType=INTEGER},


    description = #{description,jdbcType=VARCHAR},


where roleno = #{roleno,jdbcType=INTEGER}
  and rolename = #{rolename,jdbcType=VARCHAR}

update mp_role
set locked = #{locked,jdbcType=INTEGER},
  description = #{description,jdbcType=VARCHAR}
where roleno = #{roleno,jdbcType=INTEGER}
  and rolename = #{rolename,jdbcType=VARCHAR}
