mapper.select:
select

from mp_user
where 1 = 1

   and username=#{mpUserDto.username,jdbcType=VARCHAR}


   and locked=#{mpUserDto.locked,jdbcType.INTEGER}

order by userno asc
limit #{mpUserDto.start} , #{mpUserDto.size}

mapper.insert:
insert into mp_user (userno, username, password, 
  phone, register_time, locked
  )
values (#{userno,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{phone,jdbcType=VARCHAR}, #{registerTime,jdbcType=TIMESTAMP}, #{locked,jdbcType=INTEGER}
  )
